import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { MapPin, AlertTriangle, Clock, Heart, User, ThumbsUp, MessageCircle, Share2 } from 'react-feather';
import axios from 'axios';

// Contextos
import { AuthContext } from '../contexts/AuthContext';
import { LocationContext } from '../contexts/LocationContext';

// Componentes
import CatCard from '../components/Cat/CatCard';
import EmergencyCatCard from '../components/Cat/EmergencyCatCard';
import EmptyState from '../components/Shared/EmptyState';
import LoadingSpinner from '../components/Shared/LoadingSpinner';

const Home = () => {
  const { t } = useTranslation();
  const { isAuthenticated, user } = useContext(AuthContext);
  const { userLocation } = useContext(LocationContext);
  
  const [nearbyCats, setNearbyCats] = useState([]);
  const [emergencyCats, setEmergencyCats] = useState([]);
  const [recentCheckIns, setRecentCheckIns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('feed');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        
        // Fetch data based on active tab
        if (activeTab === 'nearby' && userLocation) {
          const response = await axios.get(`/api/cats/nearby`, {
            params: {
              lat: userLocation.latitude,
              lng: userLocation.longitude,
              radius: 5000 // 5km
            }
          });
          setNearbyCats(response.data);
        } else if (activeTab === 'emergency') {
          const response = await axios.get('/api/cats', {
            params: {
              health: 'Precisa de atenção,Emergência',
              status: 'Ativo',
              sort: '-updatedAt'
            }
          });
          setEmergencyCats(response.data.cats);
        } else if (activeTab === 'recent' && isAuthenticated) {
          const response = await axios.get('/api/checkins/user', {
            headers: {
              'x-auth-token': localStorage.getItem('token')
            }
          });
          setRecentCheckIns(response.data.checkIns);
        } else if (activeTab === 'feed') {
          // Fetch feed posts (combine cats and recent updates)
          const [catsResponse, checkinsResponse] = await Promise.all([
            axios.get('/api/cats', {
              params: {
                sort: '-createdAt',
                limit: 10
              }
            }),
            axios.get('/api/checkins', {
              params: {
                limit: 15,
                sort: '-createdAt'
              }
            })
          ]);
          
          // Combine and sort by date
          const combinedFeed = [
            ...(catsResponse.data.cats || []).map(cat => ({
              ...cat,
              type: 'cat',
              timestamp: new Date(cat.createdAt)
            })),
            ...(checkinsResponse.data.checkIns || []).map(checkin => ({
              ...checkin,
              type: 'checkin',
              timestamp: new Date(checkin.createdAt)
            }))
          ].sort((a, b) => b.timestamp - a.timestamp);
          
          setRecentCheckIns(combinedFeed);
        }
        
        setLoading(false);
      } catch (error) {
        console.error('Erro ao carregar dados:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [activeTab, userLocation, isAuthenticated]);

  return (
    <div className="home-page">
      <div className="welcome-banner">
        <h1>{t('home.welcome')}</h1>
        <p>{t('app.slogan')}</p>
      </div>
      
      <div className="tabs-section">
        <div className="tab-buttons">
          <button 
            className={`tab-btn ${activeTab === 'feed' ? 'active' : ''}`}
            onClick={() => setActiveTab('feed')}
          >
            <Heart size={20} />
            <span>Feed</span>
          </button>
          <button 
            className={`tab-btn ${activeTab === 'nearby' ? 'active' : ''}`}
            onClick={() => setActiveTab('nearby')}
          >
            <MapPin size={20} />
            <span>{t('home.nearbyCats')}</span>
          </button>
          <button 
            className={`tab-btn ${activeTab === 'emergency' ? 'active' : ''}`}
            onClick={() => setActiveTab('emergency')}
          >
            <AlertTriangle size={20} />
            <span>{t('home.emergencies')}</span>
          </button>
          <button 
            className={`tab-btn ${activeTab === 'recent' ? 'active' : ''}`}
            onClick={() => setActiveTab('recent')}
          >
            <Clock size={20} />
            <span>{t('home.myCheckIns')}</span>
          </button>
        </div>
        
        <div className="tab-content">
          {loading ? (
            <LoadingSpinner />
          ) : (
            <>
              {activeTab === 'feed' && (
                <div className="feed-container">
                  {recentCheckIns.length > 0 ? (
                    recentCheckIns.map((item, index) => (
                      <div key={item._id || index} className="feed-card">
                        {item.type === 'cat' ? (
                          <>
                            <div className="feed-card-header">
                              <img 
                                src={item.discoveredBy?.profilePicture || '/assets/avatars/cat-avatar-1.png'} 
                                alt={item.discoveredBy?.name || 'Usuário'} 
                                className="avatar" 
                              />
                              <div>
                                <h3>{item.discoveredBy?.name || 'Usuário'}</h3>
                                <p className="feed-time">
                                  <Clock size={14} />
                                  {new Date(item.createdAt).toLocaleDateString()}
                                </p>
                                <p className="feed-type">Registrou um novo gato</p>
                              </div>
                            </div>
                            <div className="feed-photo-container">
                              <img 
                                src={item.photoUrl} 
                                alt={item.name} 
                                className="feed-photo" 
                              />
                            </div>
                            <div className="feed-card-content">
                              <h3>{item.name}</h3>
                              <p className="feed-description">{item.description}</p>
                              <div className="feed-info">
                                <span className={`cat-health ${item.health.toLowerCase().replace(/\s+/g, '-')}`}>
                                  {item.health}
                                </span>
                                <span className="cat-location">
                                  <MapPin size={14} />
                                  {item.location?.address || 'Localização não especificada'}
                                </span>
                              </div>
                              <div className="feed-actions">
                                <Link to={`/cat/${item._id}`} className="view-details-btn">
                                  Ver detalhes
                                </Link>
                                <Link to={`/checkin/${item._id}`} className="checkin-btn">
                                  Fazer Check-in
                                </Link>
                              </div>
                            </div>
                          </>
                        ) : (
                          <>
                            <div className="feed-card-header">
                              <img 
                                src={item.user?.profilePicture || '/assets/avatars/cat-avatar-1.png'} 
                                alt={item.user?.name || 'Usuário'} 
                                className="avatar" 
                              />
                              <div>
                                <h3>{item.user?.name || 'Usuário'}</h3>
                                <p className="feed-time">
                                  <Clock size={14} />
                                  {new Date(item.createdAt).toLocaleDateString()}
                                </p>
                                <p className="feed-type">Fez um check-in com {item.cat?.name}</p>
                              </div>
                            </div>
                            <div className="feed-photo-container">
                              {item.photosUrl && item.photosUrl.length > 0 ? (
                                <img 
                                  src={item.photosUrl[0]} 
                                  alt="Check-in" 
                                  className="feed-photo" 
                                />
                              ) : (
                                <img 
                                  src={item.cat?.photoUrl} 
                                  alt={item.cat?.name} 
                                  className="feed-photo" 
                                />
                              )}
                            </div>
                            <div className="feed-card-content">
                              <h3>{item.cat?.name}</h3>
                              <div className="feed-actions-taken">
                                {item.actions.map((action, idx) => (
                                  <span key={idx} className="action-badge">{action}</span>
                                ))}
                              </div>
                              {item.actionsDescription && (
                                <p className="feed-description">{item.actionsDescription}</p>
                              )}
                              <div className="feed-actions">
                                <Link to={`/cat/${item.cat?._id}`} className="view-details-btn">
                                  Ver detalhes
                                </Link>
                              </div>
                            </div>
                          </>
                        )}
                        <div className="feed-card-footer">
                          <button className="feed-action-btn">
                            <ThumbsUp size={18} />
                            <span>Curtir</span>
                          </button>
                          <button className="feed-action-btn">
                            <MessageCircle size={18} />
                            <span>Comentar</span>
                          </button>
                          <button className="feed-action-btn">
                            <Share2 size={18} />
                            <span>Compartilhar</span>
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <EmptyState 
                      icon={<Heart size={48} />}
                      title="Nenhuma atividade no feed"
                      message="Seja o primeiro a registrar um gato ou fazer um check-in!"
                      actionLink="/add-cat"
                      actionText="Adicionar Gato"
                    />
                  )}
                </div>
              )}
              
              {activeTab === 'nearby' && (
                nearbyCats.length > 0 ? (
                  <div className="cat-grid">
                    {nearbyCats.map(cat => (
                      <CatCard key={cat._id} cat={cat} />
                    ))}
                  </div>
                ) : (
                  <EmptyState 
                    icon={<MapPin size={48} />}
                    title={t('home.noNearbyCats')}
                    message={t('home.noNearbyCatsMsg')}
                    actionLink="/add-cat"
                    actionText={t('home.addCat')}
                  />
                )
              )}
              
              {activeTab === 'emergency' && (
                emergencyCats.length > 0 ? (
                  <div className="cat-grid">
                    {emergencyCats.map(cat => (
                      <EmergencyCatCard key={cat._id} cat={cat} />
                    ))}
                  </div>
                ) : (
                  <EmptyState 
                    icon={<AlertTriangle size={48} />}
                    title={t('home.noEmergencies')}
                    message={t('home.noEmergenciesMsg')}
                  />
                )
              )}
              
              {activeTab === 'recent' && (
                isAuthenticated ? (
                  recentCheckIns.length > 0 ? (
                    <div className="checkin-list">
                      {recentCheckIns.map(checkIn => (
                        <div key={checkIn._id} className="checkin-card">
                          <Link to={`/cat/${checkIn.cat._id}`}>
                            <img src={checkIn.cat.photoUrl} alt={checkIn.cat.name} />
                            <div className="checkin-info">
                              <h3>{checkIn.cat.name}</h3>
                              <p>
                                <Clock className="icon" /> {new Date(checkIn.createdAt).toLocaleDateString()}
                              </p>
                              <div className="checkin-actions">
                                {checkIn.actions.map((action, index) => (
                                  <span key={index} className="action-tag">
                                    {action}
                                  </span>
                                ))}
                              </div>
                            </div>
                          </Link>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <EmptyState 
                      icon={<Clock size={48} />}
                      title={t('home.noCheckIns')}
                      message={t('home.noCheckInsMsg')}
                      actionLink="/map"
                      actionText={t('home.viewMap')}
                    />
                  )
                ) : (
                  <EmptyState 
                    icon={<Heart size={48} />}
                    title={t('home.loginToHelp')}
                    message={t('home.loginToHelpMsg')}
                    actionLink="/login"
                    actionText={t('auth.login')}
                  />
                )
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
