import React from 'react';
import { Link } from 'react-router-dom';
import './CatCard.css';

const CatCard = ({ cat }) => {
  // Calcula tempo desde o último check-in
  const getTimeSince = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

    if (diffInHours < 1) return 'Menos de 1h';
    if (diffInHours < 24) return `${diffInHours}h`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays === 1) return '1 dia';
    if (diffInDays < 30) return `${diffInDays} dias`;

    const diffInMonths = Math.floor(diffInDays / 30);
    if (diffInMonths === 1) return '1 mês';
    return `${diffInMonths} meses`;
  };

  return (
    <Link to={`/cat/${cat._id}`} className="cat-card">
      <div className="cat-image">
        <img src={cat.photoUrl} alt={cat.name} />
        <div className={`health-badge ${cat.health.toLowerCase().replace(/\s+/g, '-')}`}>
          {cat.health}
        </div>
      </div>

      <div className="cat-info">
        <h3>{cat.name}</h3>

        <div className="cat-location">
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"></path>
            <circle cx="12" cy="10" r="3"></circle>
          </svg>
          <span>{cat.location.address}</span>
        </div>

        <div className="cat-last-checkin">
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <circle cx="12" cy="12" r="10"></circle>
            <polyline points="12 6 12 12 16 14"></polyline>
          </svg>
          <span>Checkin há {getTimeSince(cat.lastCheckIn)}</span>
        </div>

        {cat.needs && cat.needs.length > 0 && (
          <div className="cat-needs">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M10.29 3.86L1.82 18a2 2 0 0 0 1.71 3h16.94a2 2 0 0 0 1.71-3L13.71 3.86a2 2 0 0 0-3.42 0z"></path>
              <line x1="12" y1="9" x2="12" y2="13"></line>
              <line x1="12" y1="17" x2="12.01" y2="17"></line>
            </svg>
            <span>Precisa de ajuda</span>
          </div>
        )}
      </div>
    </Link>
  );
};

export default CatCard;
